mapper.select:
( SELECT id, userId, `from`, `type`, content, createDate, lastEditData, createUser, lastEditUser FROM msg_record WHERE userId = #{userId} AND `from` IN
#{item}
LIMIT #{limit} ) UNION( SELECT id, userId, `from`, `type`, content, createDate, lastEditData, createUser, lastEditUser FROM msg_record WHERE userId IN
#{item}
AND `from` = #{userId} LIMIT #{limit} ) LIMIT #{limit}
